import React, {Component} from 'react';
import * as d3 from 'd3';
import _ from 'lodash';

import './App.css';

import Preloader from './components/Preloader';
import loadAllData from './DataHandling';
import CountyMap from './components/CountyMap';
import Histogram from './components/Histogram';
import {Title, Description, GraphDescription} from './components/Meta';
import MedianLine from './components/MedianLine';
import Controls from './components/Controls';

class App extends Component {
  state = {
    techSalaries: [],
    countyNames: [],
    medianIncomes: [],
    salariesFilter: () => true,
    filteredBy: {
      USstate: '*',
      year: '*',
      jobTitle: '*'
    }
  }

  countyValue(county, techSalariesMap) {
    const medianHousehold = this.state.medianIncomes[county.id];
    const salaries = techSalariesMap[county.name];
    if (!medianHousehold || !salaries) {
      return null;
    }
    const median = d3.median(salaries, d => d.base_salary);
    return {
      countyID: county.id,
      value: median - medianHousehold.medianIncome
    };
  }

  componentWillMount() {
    loadAllData(data => this.setState(data));
  }

  updateDataFilter(filter, filteredBy) {
    this.setState({
      salariesFilter: filter,
      filteredBy: filteredBy
    });
  }

  render() {
    if (this.state.techSalaries.length < 1) {
      return (
        <Preloader />
      );
    }

    const filteredSalaries = this.state.techSalaries;
    const filteredSalariesMap = _.groupBy(filteredSalaries, 'countyID');
    const countyValues = this.state.countyNames.map(county => this.countyValue(county, filteredSalariesMap)).filter(d => !_.isNull(d));

    let zoom = null;
    let medianHousehold = this.state.medianIncomesByUSState['US'][0].medianIncome;

    return (
      <div className="App container">
        <Title data={filteredSalaries} filteredBy={this.state.filteredBy} />
        <Description data={filteredSalaries}
                     allData={this.state.techSalaries}
                     medianIncomesByCount={this.state.medianIncomesByCount}
                     filteredBy={this.state.filteredBy} />
        <GraphDescription data={filteredSalaries}
                          filteredBy={this.state.filteredBy} />
        <svg width="1100" height="500">
          <CountyMap usTopoJson={this.state.usTopoJson} 
                     USstateNames={this.state.USstateNames} 
                     values={countyValues} 
                     x={0} 
                     y={0} 
                     width={500} 
                     height={500} 
                     zoom={zoom} />
          <Histogram bins={10}
                     width={500}
                     height={500}
                     x="500"
                     y="10"
                     data={filteredSalaries}
                     axisMargin={83}
                     bottomMargin={5}
                     value={d => d.base_salary} />
          <MedianLine data={filteredSalaries}
                      x={500}
                      y={10}
                      width={600}
                      height={500}
                      bottomMargin={5}
                      median={medianHousehold}
                      value={d => d.base_salary} />
        </svg>
      </div>
    );
  }
}

export default App;
